(function($){'use strict'
$(window).on('load',function(){$('.preloader').fadeOut(100)})
$(window).scroll(function(){if($('.navigation').offset().top>100){$('.navigation').addClass('nav-bg')}else{$('.navigation').removeClass('nav-bg')}})
$('[data-background]').each(function(){$(this).css({'background-image':'url('+$(this).data('background')+')'})})
$('[data-color]').each(function(){$(this).css({'background-color':$(this).data('color')})})
$('[data-progress]').each(function(){$(this).css({'bottom':$(this).data('progress')})})})(jQuery)